var Handlebars = require('handlebars');
var javarules = require('./javarules');

//convert a data type to a valid Java one
Handlebars.registerHelper('javaDataType', javarules.dataType);

//convert a name to a valid Java class name
Handlebars.registerHelper('javaClassName', javarules.className);

//convert a name to a valid Java method name
Handlebars.registerHelper('javaMethodName', javarules.methodName);

//lookup and resolve references to data types, will return the Java class name
Handlebars.registerHelper('refLookup', function(ref) {
  var parts = ref.split("/");
  return javarules.className(parts[parts.length - 1]);
});

//lookup and resolve references to data types, will return the Java class name
Handlebars.registerHelper('tag', function(context) {
  return "{{" + context + "}}";
});

//allow slightly more sophisticated inclusion by checking the value of a property, not just it's presence or absence
Handlebars.registerHelper('has', function(context, options, handler) {
  //see if the current context matches the options passed in
  if (context == options) {
    var frame = undefined;
    if (handler.data) {
      //this section allows the contents of the tag to be passed through to the processor
      //but as this is a stack, need to create a new stack frame for this call so that it
      //is popped correctly
      frame = {};
      for (var prop in handler.data) {
        //clone the object as we want to add a _parent property
        if (handler.data.hasOwnProperty(prop)) {
            frame[prop] = handler.data[prop];
        }
      }
      frame._parent = handler.data;
    }
    //call down and process the contents of the block
    return handler.fn(handler.data.root, {
      data: frame,
      blockParams: [handler.data.root]
    });
  }
  //parameters didn't match, so don't render anything in the template
  return undefined;
});

//convert tag contents to lower case
Handlebars.registerHelper('toLowerCase', function(context) {
  return String(context).toLowerCase();
});

//convert tag contents to upper case
Handlebars.registerHelper('toUpperCase', function(context) {
  return String(context).toUpperCase();
});

Handlebars.registerHelper('firstAvailable', function() {
  for(var i=0; i < arguments.length; i++) {
    if (arguments[i] && typeof arguments[i] === 'string') {
      return arguments[i];
    }
  }
  return "undefined";
});

module.exports = {
  //module doesn't export anything, all helpers are installed by requiring this module
}

module to provide rules for generating java artifacts

function convertToJavaName(name, isClass) {
  if(!name.length) return "anonymous";
  name = name.trim();
  var result = "";
  for(var i = 0; i < name.length; i++) {
    var c = name.charAt(i);
    if(i == 0) {
      if(isClass) {
        c = c.toUpperCase();
      } else {
        c = c.toLowerCase();
      }
    }
    switch(c) {
      case ' ' :
        result += "_";
        break;
      default:
        result += c;
        break;
    }
  }
  return result;
}

var methodName = function(name) {
  return convertToJavaName(name, false);
}

var className = function(name) {
  return convertToJavaName(name, true);
}

var dataType = function(type) {
  if(type === "string") return "String";
  if(type === 'integer') return "long";
  return "Object";
}

module.exports = {
  methodName : methodName,
  className : className,
  dataType : dataType
};

module for logging capabilities, shared across the generator

var log = [];

var writeToLog = function(header, data) {
  if(!data) {
    log.push(header + " : undefined");
    return;
  }
  if((typeof data === 'string') || (typeof data === 'number') || (typeof data === 'boolean')) {
    log.push(header + " : " + data);
  } else {
    log.push(header + " : " + JSON.stringify(data, null, 2) + JSON.stringify(Object.getOwnPropertyNames(data), null, 2));
  }
}

var getLogs = function() {
  return log;
}

//clear any log entries
var clear = function() {
  log = [];
}

module.exports = {
  //module doesn't export anything, all helpers are installed by requiring this module
  writeToLog : writeToLog,
  getLogs : getLogs,
  clear : clear
}

use strict'

function PromptManager(config) {
  this.prompts = [];
  this.types = {};
  this.prompt = {
    type    : 'list',
    name    : 'promptType',
    message : 'Select from the list of available generation options.\n',
    choices : []
  };
  this.config = config;
}

PromptManager.prototype.add = function(name) {
  var Ext = require('../prompts/' + name + '.js');
  var ext = new Ext(this.config);
  this.prompts.push(ext);

  var value = ext.getChoice();
  if(value) {
    this.prompt.choices.push(value);
  }

  //create an index of createType by extension
  var extqs = ext.getQuestions();
  for(var i = 0; i < extqs.length; i++) {
    if(extqs[i].name === 'createType') {
      for(var j = 0; j < extqs[i].choices.length; j++) {
        this.types[extqs[i].choices[j].value] = ext;
      }
      break;
    }
  }
}

PromptManager.prototype.getQuestions = function() {
  var questions = [];
  questions.push(this.prompt);
  for(var i = 0; i < this.prompts.length; i++) {
    questions = questions.concat(this.prompts[i].getQuestions());
  }
  return questions;
}

//this was run headless, so need to work out which extension provides the selected createType
PromptManager.prototype.setExtension = function(createType) {
  for (var type in this.types) {
    if (this.types.hasOwnProperty(type)) {
      if(type === createType) {
        answers.promptType = this.types[type].id;
        break;
      }
    }
  }
}

PromptManager.prototype.afterPrompt = function(answers, config) {
  for(var i = 0; i < this.prompts.length; i++) {
    var prompt = this.prompts[i];
    var questions = prompt.getQuestions();
    var promptAnswers = {};
    for(var j = 0; j < questions.length; j++) {
      var name = questions[j].name;
      if(answers[name]) {
        promptAnswers[name] = answers[name];
      }
    }
    if(Object.keys(promptAnswers)) {
      this.prompts[i].afterPrompt(promptAnswers, config);
    }
  }
}

module.exports = exports = PromptManager;

