    for(var i = 1; i < config.frameworkDependencies.length; i++) {
      dependencies.push(config.frameworkDependencies[i].name);
    }
    assert.equal(dependencies.length, 2)
    assert(dependencies.includes('testFrameworkDep'), 'dependencies=' + dependencies);
    assert(dependencies.includes('testOtherFrameworkDep'), 'dependencies=' + dependencies);
  });
});

test the controller

var assert = require('assert');
var Control = require('../../generators/lib/control');
var path = require('path');
var Config = require("../../generators/lib/config");
var config = new Config();

before(function() {
  config.templateRoot = path.resolve("./test/resources/control");
});

describe('control library', function() {

  describe('can create instances', function() {
    it('it should throw an exception if the path is missing', function() {
      assert.throws(()=>{new Control()})
    });
    it('it should throw an exception if the config is missing', function() {
      assert.throws(()=>{new Control("Path 1")})
    });
    it('it should be possible to have more than one instance of a control', function(){
      var control = new Control("Path 1", config);
      var control2 = new Control("Path 2", config);
      assert.equal(false, control.getPath() === control2.getPath());
    });
  });

  describe('can find a control file', function() {
    it('it should find the control.js file in the root', function(){
      config.templateFullPath = path.resolve("./test/resources/control/with-control");
      console.log("Config : " + JSON.stringify(config));
      var control = new Control(config.templateFullPath, config);
      assert.equal(true, control.hasControl());
    });

    it('it should ignore any control.js file not found in the root', function(){
      config.templateFullPath = path.resolve("./test/resources/control/without-control");
      var control = new Control(config.templateFullPath, config);
      assert.equal(false, control.hasControl());
    });

    it('it should throw an exception when the contol.js file does not contain valid javascript', function(){
      config.templateFullPath = path.resolve("./test/resources/control/with-invalid-control");
      assert.throws(() => {new Control(config.templateFullPath, config)});
    });
  });

  describe('process a control file', function() {
    it('it should exclude a file in the file exclusion list', function(){
      config.templateFullPath = path.resolve("./test/resources/control/with-control");
      var control = new Control(config.templateFullPath, config);
      assert.equal(false, control.shouldGenerate("build.gradle"));
    });

    it('it should include any file not in the file exclusion list', function(){
      config.templateFullPath = path.resolve("./test/resources/control/with-control");
      var control = new Control(config.templateFullPath, config);
      assert.equal(true, control.shouldGenerate("somefile.txt"));
    });

    it('it should exclude a directory in the directory exclusion list', function(){
      config.templateFullPath = path.resolve("./test/resources/control/with-control");
      var control = new Control(config.templateFullPath, config);
      assert.equal(false, control.shouldGenerate("donotprocess/file1.txt"));
    });

    it('it should support a composition element', function(){
      config.templateFullPath = path.resolve("./test/resources/control/with-control");
      var control = new Control(config.templateFullPath, config);
      assert(Array.isArray(control.getComposition()));
      assert(control.getComposition()[0].includes("subTemplate"));
    });

    it('it should support not having a composition element', function(){
      config.templateFullPath = path.resolve("./test/resources/control/no-composition");
      var control = new Control(config.templateFullPath, config);
      assert(Array.isArray(control.getComposition()));
      assert.equal(0, control.getComposition().length);
    });
  });

  describe('process found files', function() {
    it('it should use a custom fileFound callback if defined', function(){
      config.templateFullPath = path.resolve("./test/resources/control/with-control");
      var control = new Control(config.templateFullPath, config);
      var fragments = control.fileFound("path", "contents")
      assert.equal(1, fragments.length);
      assert.equal(fragments[0].path, "alteredpath");
    });

    it('it should use the default callback if no custom one is defined', function(){
      config.templateFullPath = path.resolve("./test/resources/control/without-excludes");
      var control = new Control(config.templateFullPath, config);
      var fragments = control.fileFound("path", "contents")
      assert.equal(1, fragments.length);
      assert.equal(fragments[0].path, "path");
    });
  });

  describe('generates file', function() {
    it('it should generate a file if a control block is not present', function(){
      config.templateFullPath = path.resolve("./test/resources/control/without-control");
      var control = new Control(config.templateFullPath, config);
      assert.equal(true, control.shouldGenerate());
    });

    it('it should generate a file if a control block present, but has no exclusions defined', function(){
      config.templateFullPath = path.resolve("./test/resources/control/without-excludes");
      var control = new Control(config.templateFullPath, config);
      assert.equal(true, control.shouldGenerate());
    });
  });

});

test the defaults module

var assert = require('assert');
var defaults = require('../../generators/lib/defaults');

describe('Defaults module', function() {
  describe('Call getDefault to get the default object', function() {
    var appName = defaults.getObject('appName');
    var buildType = defaults.getObject('buildType');
    it('can get the default value', function() {
      assert.equal(appName.default, 'LibertyProject');
      assert.equal(buildType.default, 'maven');
    });
    it('can get the description', function() {
      assert.equal(appName.desc, 'Name of the application');
      assert.equal(buildType.desc, 'Build system to use');
    });
    it('can get the type', function() {
      assert.equal(appName.type, String);
      assert.equal(buildType.type, String);
    });
  });
  it('can use getDefaultValue to get the default value', function() {
    assert.equal(defaults.get('appName'), 'LibertyProject');
    assert.equal(defaults.get('buildType'), 'maven');
  });
  describe('Call getDefaults to get a list of config values with defaults', function() {
    var defaultValues = defaults.get();
    var foundAppName = false;
    var foundBuildType = false;
    for(var i = 0; i < defaultValues.length; i++) {
      if(defaultValues[i] === 'appName') {
        foundAppName = true;
      }
      if(defaultValues[i] === 'buildType') {
        foundBuildType = true;
      }
    }
    it('finds appName in the list of config values with defaults', function() {
      assert(foundAppName);
    });
    it('finds buildType in the list of config values with defaults', function() {
      assert(foundBuildType);
    });
  });
  describe('Bluemix default type is a function', function() {
    var bluemixType = defaults.getObject('bluemix').type;
    it('returns a function', function() {
      assert.equal(typeof bluemixType, 'function');
    });
    it('returns a function which returns a JSON object when passed a string', function() {
      var string = '{"name":"bob"}';
      var json = bluemixType(string);
      assert.equal(typeof json, 'object');
      assert.equal(json.name, 'bob');
    });
    it('returns a function which returns a JSON object when passed an object', function() {
      var object = JSON.parse('{"name":"bob"}');
      var json = bluemixType(object);
      assert.equal(typeof json, 'object');
      assert.equal(json.name, 'bob');
    });
  });
})

test the fs (file system) processor

var fs = require('fs');
var assert = require('assert');
var processor = require('../../generators/lib/fsprocessor');
var path = require('path');
var Config = require('../../generators/lib/config');

describe('fsprocessor library', function() {

  describe('processor does not allow invalid parameters', function() {
    it('it should throw an exception if the paths is not an array', function() {
      processor.paths = "string";
      assert.throws(()=>{processor.scan()})
    });
    it('it should throw an exception if the paths is an empty array', function() {
      processor.paths = [];
      assert.throws(()=>{processor.scan()})
    });
    it('it should throw an exception if the config is missing', function() {
      processor.paths = ["./test/resources/fsprocessor/test-templates-1file"];
      assert.throws(()=>{processor.scan()})
    });
  });

  describe('processor provides file operations', function() {
    it('it should throw an exception if the file does not exist', function() {
      assert.throws(()=>{processor.getContentsSync("madeupfilename.txt")})
    });
    it('it should throw an exception if the file is a directory', function() {
      assert.throws(()=>{processor.getContentsSync("./test/resources")})
    });
    it('it should get the contents of a file as a string', function() {
      var contents = processor.getContentsSync("./test/resources/fsprocessor/test-templates-1file/file-1.txt");
      assert.equal('string', typeof contents);
      assert(contents.indexOf('sample text file'));
    });
    it('it should get the contents of a file as a JSON object', function() {
      var contents = processor.getContentsSync("./test/resources/fsprocessor/sample.json");
      assert.equal('object', typeof contents);
      assert(contents.value);
    });
  });

  describe('walk tree specified with relative path', function() {
    it('it should walk a file system tree calling back when a file is found', function(done){
      processor.paths = ["./test/resources/fsprocessor/test-templates-1file"];
      processor.scan(new Config(), (relativePath, contents) => {
        assert.equal('file-1.txt', relativePath);
      }).then(() => { done(); })
        .catch((err) => { done(err);});
    });
    it('it should throw an exception for a non-existant path', function(done){
      processor.paths = ["./test/resources/fsprocessor/test-folder-does-not-exist"];
      processor.scan(new Config(), (relativePath, contents) => {
        assert.fail(false, true, "Should not have found any projects");
      }).then(() => { assert.fail(false, true, "Walk should not have completed without error"); })
        .catch((err) => {
          //this error is expected
          done();
        });
    });
    it('it should throw an exception when unable to read a file', function(done){
      if (path.delimiter === ';') {
        //can't check on Windows as can't set file permissions
        done();
        return;
      }
      var root = "./test/resources/fsprocessor/test-templates-badfile";
      var fpath = "/filewithnoreadperms.txt";
      fs.chmodSync(root + fpath, 222);
      processor.paths = [root];
      processor.scan(new Config(), (relativePath, contents) => {
        fs.chmodSync(root + fpath, 755);
        assert.fail(false, true, "Should not have found any projects");
      }).then(() => {
        fs.chmodSync(root + fpath, 755);
        assert.fail(false, true, "Walk should not have completed without error");
      }).catch((err) => {
        fs.chmodSync(root + fpath, 755);
        //this error is expected
        done();
      });
    });
  });

  describe('walk tree specified with an absolute path', function() {
    it('it should walk a file system tree calling back when a file is found', function(done){
      //path.resolve will convert to an absolute path
      processor.paths = [path.resolve("./test/resources/fsprocessor/test-templates-1file")];
      processor.scan(new Config(), (relativePath, contents) => {
        assert.equal('file-1.txt', relativePath);
      }).then(() => { done(); })
        .catch((err) => { done(err);});
    });
  });

  describe('walk tree ', function() {
    it('it should walk a file system tree (with empty dirs) calling back when a file is found', function(done){
      //these are the files that should be found
      var files = ['file-1.txt', 'file-2.txt', 'file-3.txt', 'file-4.txt', 'file-5.txt'];
      var separator = (path.delimiter === ';' ? "\\" : "/");
      var unknown = [];
      processor.paths = [path.resolve("./test/resources/fsprocessor/test-templates-emptyDirs"),
                        path.resolve("./test/resources/fsprocessor/test-templates-second")];
      processor.scan(new Config(), (relativePath, contents) => {
        for(var i = 0; i  {
        if((unknown.length == 0) && (files.length == 0)) {
          done();   //all done OK
        } else {
          done("Files not found or not expected : files = " + JSON.stringify(files) + ", unknown = " + JSON.stringify(unknown));
        }
      }).catch((err) => { done(err);});
    });
  });
});

